      backgroundColor: Colors.white,
      body: ListView.builder(
        itemCount: motywyLiterackie.length,
        itemBuilder: (BuildContext context, int index) {
          return Column(
            children: [
              Padding(
                padding: EdgeInsetsGeometry.symmetric(
                  horizontal: 10,
                  vertical: 5,
                ),
                child: SizedBox(
